import React, { useContext, useState } from 'react';
import "./styles.scss";
import { MoreVert } from '@mui/icons-material';
import ClickOutComponent from "react-onclickout"
import storeApi from '../../utils/storeApi';

const Title = ({title, listid}) => {

  const [open, setOpen] = useState(false);
  const [openOptions, setOpenOptions] = useState(false);
  const [newTitle, setNewTitle] = useState(title);
  const { updateListTitle, deleteList } = useContext(storeApi);

  const handOnBlur =()=> {
    setOpen((prev)=>!prev)
  }

  return (
    <>
    {
      open ? (
        <div>
          <input type='text' className='input-title' value={newTitle} onChange={(e)=>setNewTitle(e.target.value)} 
          onBlur={handOnBlur}
          onKeyDown={(e)=>{
            if(e.key === "Enter"){
              handOnBlur()
            }
            return
          }}
          />
        </div>
      ): (
        <div className='editable-title-container'>
         <h2 onClick={()=>setOpen(prev=>!prev)} className='editable-title'>{title}</h2>
         <button className='list-button' onClick={()=>setOpenOptions(prev=>!prev)}>
          <MoreVert/>
         </button>
         {
          openOptions && (
            <ClickOutComponent onClickOut={(e)=>{
              setOpenOptions((prev)=>!prev)
            }}>
              <ul className='menu-card'>
                <li onClick={()=>{
                  setOpenOptions((prev)=>!prev)
                }}>Delete List</li>
                <li onClick={()=>{
                  setOpenOptions((prev)=>!prev)
                }}>Edit Card Title</li>
              </ul>
            </ClickOutComponent>
          )
         }
        </div>
      )
    }
    </>
  )
}

export default Title;
