import React, { useState } from 'react';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import sample from "../../utils/sample"
import List from '../List';
import InputContainer from '../InputContainer';
import {v4 as uuid} from "uuid";
import {addDoc, arrayUnion, collection, deleteDoc, doc, updateDoc} from "firebase/firestore";
import storeApi from '../../utils/storeApi';
import {db, timestamp} from "../../firebase";

const Home = () => {
  const [lists, setLists] = useState(sample.lists);

  const addMoreCard = async(title,listId)=> {

    if(!title){
      return;
    }
    const newCardId = uuid();
    const newCard = {
      id:newCardId,
      title
    };

    const listRef = doc(db, "lists", listId);

    await updateDoc(listRef, {
      cards: arrayUnion(newCard),
    });
  };

  const removeCard = (index, listId, cardId) => {
    const listRef = doc(db, "lists", listId);

    lists.forEach(async(list)=> {
      if(list.id === listId){
        list.cards.splice(index,1)
        await updateDoc(listRef,{
          cards:list.cards.filter((card)=>card.id!==cardId)
        });
      }
      return list;
    });
  };

  const updateCardTitle = (title, index, listId, cardId)=>{
    const listRef = doc(db, "lists", listId);

    lists.forEach(async(list)=> {
      if(list.id===listId){
        list.cards[index].title=title;
        await updateDoc(listRef,{
          cards:list.cards.map((card)=>{
          if(card.id === cardId){
            card.title = title;
            return card;
          }
          return card;
          })
        })
      }
      return list;
    })
  }

  const addMoreList = async(title)=> {
    if(!title){
      return;
    }
    await addDoc(collection(db, "lists"),{
      title,
      cards:[],
      timestamp
    })
  }

  const updateListTitle = (title,listId)=> {
    const listRef = doc(db,"lists",listId);

    lists.forEach(async(list)=> {
      if(list.id===listId){
        list.title = title;
        await updateDoc(listRef,{
          title:title
        })
      }
      return list;
    })
  }

  const deleteList = async(listId)=> {
    await deleteDoc(doc(db,"lists",listId));
  };

  const onDragEnd = async(result)=> {
  const {destination, source, draggableId, type} = result;

  if(!destination){
    return;
  }
  if(type==="list"){
    const destinationRef = doc(db,"lists", lists[destination.index].id)
    const sourceRef = doc(db,"lists", lists[source.index].id)

    await updateDoc(destinationRef,{
      timestamp:lists[source.index].timestamp
    })
    await updateDoc(sourceRef,{
      timestamp:lists[destination.index].timestamp
    });
    return;
  }
  }

  return(
    <storeApi.Provider
    value={
      {
        addMoreCard,
        addMoreList,
        updateListTitle,
        removeCard,
        updateCardTitle,
        deleteList
      }
    }
    >
      <DragDropContext onDragEnd={onDragEnd}>
       <Droppable droppableId="app" type="list" direction="horizontal">
       {(provided)=> (
          <div className="wrapper" ref={provided.innerRef}> 
            {
              lists.map((list, index)=> {
                return <List list={list} key={list.id} index={index} />;
              })
            }
            <div> 
              <InputContainer type="list"/>
             </div>
            {provided.placeholder}

          </div>
        )
       }
       </Droppable>
      </DragDropContext>
    </storeApi.Provider>
  ); 
};

export default Home;
